mapper.select:
    select 
    a.svn_repo_id, a.svn_repo_name, a.svn_repo_path, a.svn_repo_url, a.svn_repo_des, 
    b.svn_user_id, b.svn_user_name, b.svn_password,
    b.user_status, b.svn_user_create_date, b.svn_user_modify_date
    from svn_repo a join svn_user b on (a.svn_repo_id = b.svn_repo_id)
    where b.svn_user_name = #{svnUserName}
    
		limit #{offset}, #{limit}

	select count(*) 
	from svn_repo a join svn_user b on (a.svn_repo_id = b.svn_repo_id) 
    left join sys_user c on (b.svn_user_name = c.username)
    
	    
			and a.svn_repo_name = #{svnRepoName}
		
		
			and c.name = #{name}
		
		
			and b.svn_user_name like concat('%', #{svnUserName}, '%')

    select
    a.svn_repo_id, a.svn_repo_name, a.svn_repo_path, a.svn_repo_url, a.svn_repo_des, 
    b.svn_user_id, b.svn_user_name, b.svn_password,
    b.user_status, b.svn_user_create_date, b.svn_user_modify_date, 
    c.name
    from svn_repo a join svn_user b on (a.svn_repo_id = b.svn_repo_id) 
    left join sys_user c on (b.svn_user_name = c.username)
    
	    
			and a.svn_repo_name = #{svnRepoName}
		
		
			and c.name = #{name}
		
		
			and b.svn_user_name like concat('%', #{svnUserName}, '%')
		
	
	
		
			order by ${sort} ${order}
		
		
			order by a.svn_repo_id desc
		
	
    
		limit #{offset}, #{limit}

select svn_user_id
from svn_user
where svn_user_name = #{svnUserName} and svn_repo_id = #{svnRepoId}

select svn_user_id
from svn_user
where svn_user_name = #{svnUserName}
